query PrescriptionsInformation($sqon: JSON, $first: Int, $offset: Int, $sort: [Sort], $searchAfter: JSON) {
  Analyses {
    hits(
      filters: $sqon
      first: $first
      offset: $offset
      sort: $sort
      searchAfter: $searchAfter
    ) {
      edges {
        searchAfter
        node {
          id
          assignments
          patient_id
          patient_mrn
          prescription_id
          ep
          created_on
          timestamp
          requester
          prenatal
          ldm
          tasks
          analysis_code
          status
          priority
          project_code
          sequencing_requests {
            hits {
              edges {
                node {
                  patient_relationship
                  patient_disease_status
                  task_runname
                  __typename
                }
                __typename
              }
              __typename
            }
            __typename
          }
          __typename
        }
        __typename
      }
      total
      __typename
    }
    aggregations(filters: $sqon) {
      priority {
        buckets {
          key
          key_as_string
          doc_count
          __typename
        }
        __typename
      }
      analysis_code {
        buckets {
          key
          key_as_string
          doc_count
          __typename
        }
        __typename
      }
      project_code {
        buckets {
          key
          key_as_string
          doc_count
          __typename
        }
        __typename
      }
      tasks {
        buckets {
          key
          key_as_string
          doc_count
          __typename
        }
        __typename
      }
      status {
        buckets {
          key
          key_as_string
          doc_count
          __typename
        }
        __typename
      }
      sequencing_requests__status {
        buckets {
          key
          key_as_string
          doc_count
          __typename
        }
        __typename
      }
      ldm {
        buckets {
          key
          key_as_string
          doc_count
          __typename
        }
        __typename
      }
      ep {
        buckets {
          key
          key_as_string
          doc_count
          __typename
        }
        __typename
      }
      prenatal {
        buckets {
          key
          key_as_string
          doc_count
          __typename
        }
        __typename
      }
      sequencing_requests__patient_relationship {
        buckets {
          key
          key_as_string
          doc_count
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
